# coding=utf-8import unittestimport osfrom time import sleepimport requests as reimport randomimport public.methods as timport public.case_xls as xlclass API(unittest.TestCase,t.Methods,xl.Case_xls):	u'''常见问题列表'''	def setUp(self):		self.headers = {self.get_row(0, 11)[0]: self.get_row(0, 11)[1]}  # headers		self.url = self.get_row(1,1)[1]		self.key = self.get_row(1,2)        # key	def tearDown(self):		pass	def test_01(self):		u'''常见问题列表,正确必填参数'''		value = self.get_row(1,3)         # value		data={}		data[self.key[2]] = self.get_token()		request = self.apicall(value[1],self.url,None,self.headers,data)		print (request.status_code)		self.assertEqual(request.status_code,int(value[8]))		print (request.json())		self.assertEqual(request.json()['data'][0]['id'],unicode(value[9]))	def test_02(self):		u'''常见问题,错误必填参数'''		value = self.get_row(1,4)         # value		data={}		data[self.key[2]] = self.get_token()+'1'		request = self.apicall(value[1], self.url, None, self.headers, data)		print (request.status_code)		self.assertEqual(request.status_code, int(value[8]))		print (request.json())		self.assertEqual(request.json()['error_code'], int(value[9]))	def test_03(self):		u'''常见问题,错误选填参数(current)'''		value = self.get_row(1,5)         # value		data = {}		data[self.key[2]] = self.get_token()		data[self.key[3]] =value[3]		request = self.apicall(value[1], self.url, None, self.headers, data)		print (request.status_code)		self.assertEqual(request.status_code, int(value[8]))		print (request.json())		self.assertEqual(request.json()['error_code'], int(value[9]))	def test_04(self):		u'''常见问题, 错误选填参数(pagesize)'''		value = self.get_row(1,6)         # value		data = {}		data[self.key[2]] = self.get_token()		data[self.key[4]] = value[4]		request = self.apicall('GET', self.url, None, self.headers, data)		print (request.status_code)		self.assertEqual(request.status_code, int(value[8]))		print (request.json())		self.assertEqual(request.json()['error_code'], int(value[9]))if __name__ == '__main__':	unittest.main()